function BinaryParticleSystem() {
  const canvas = document.createElement("canvas");
  document.body.appendChild(canvas);
  const ctx = canvas.getContext("2d");

  // Set canvas to cover the whole window and behind content
  canvas.style.position = "fixed";
  canvas.style.top = "0";
  canvas.style.left = "0";
  canvas.style.zIndex = "-1"; // Place canvas behind all content
  canvas.style.width = "100%";
  canvas.style.height = "100%";
  canvas.style.pointerEvents = "none"; // Make it non-interactive

  function resizeCanvas() {
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
  }
  resizeCanvas();
  window.addEventListener("resize", resizeCanvas);

  const particles = [];
  const numParticles = 100; // Number of particles

  // Initialize particles
  for (let i = 0; i < numParticles; i++) {
    particles.push({
      x: Math.random() * canvas.width,
      y: Math.random() * canvas.height,
      size: Math.random() * 20 + 10, // Size of the digits
      vx: (Math.random() - 0.5) * 2,
      vy: (Math.random() - 0.5) * 2,
      value: Math.random() < 0.5 ? "0" : "1", // Randomly assign 0 or 1
    });
  }

  // Animation loop
  function animate() {
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    particles.forEach((particle) => {
      particle.x += particle.vx;
      particle.y += particle.vy;

      // Wrap around edges
      if (particle.x < 0) particle.x = canvas.width;
      if (particle.x > canvas.width) particle.x = 0;
      if (particle.y < 0) particle.y = canvas.height;
      if (particle.y > canvas.height) particle.y = 0;

      // Draw the binary digit (0 or 1) as a particle
      ctx.font = `${particle.size}px monospace`; // Monospace font for a digital effect
      ctx.fillStyle = "#00FF00"; // Green color for "0" and "1"
      ctx.fillText(particle.value, particle.x, particle.y);
    });

    requestAnimationFrame(animate);
  }

  animate();
}

// Initialize the binary particle system on page load
document.addEventListener("DOMContentLoaded", BinaryParticleSystem);
